import React, { Component } from 'react';

class clock extends React.Component{

  componentDidMount(){
    const canvas=this.refs.canvas;
    const can=canvas.getContext('2d');
    var no = 0;
    var poinTofill = -Math.PI/2;
    var diff;
    var time=5*60;
    var minutes=4;
    var second=59;
    var fill;

    function circle() {
        diff = -Math.PI/2+((no / time) * Math.PI * 2);
        can.clearRect(0, 0, 500, 500);
        if(second===0){
            --minutes;
            second=60;
        }
        var t=`${minutes}:${second}`;
        can.lineWidth = 6;
        can.fillStyle = "#fff";
        can.strokeStyle = "red";
        can.textAlign = "center";
        can.font = "25px Monspace";
        can.fillText("Session", 250, 180);
        can.fillText(t, 250, 250);
        can.beginPath();
        can.arc(250, 250, 150, poinTofill, Math.PI*2, false)
        can.stroke();
        can.lineWidth = 5;
        can.strokeStyle = "#fff";
        can.beginPath();
        can.arc(250, 250, 150, poinTofill, diff,false );
        can.stroke();
        can.save();
        if (no===time) {
            clearTimeOut(fill);
        }

        ++no;
        --second;
    }
    circle();
  }
  render(){
  return (<div>
    <canvas refs="canvas" width="300" height="300"></canvas>
    <div className="controler">
        <button id="start" type="submit">Start</button>
        <button id="stop">Stop</button>
        <button id="reset">Reset</button>
    </div>
    </div>
  )
}
}

export default clock;
