import React, { Component } from 'react';

class clock extends React.Component{
  constructor(props){
    super(props);
    this.draw=this.draw.bind(this);
    this.trigger=this.trigger.bind(this);
    this.circle=this.circle.bind(this);
  }
circle(){
  // alert("hell");
  const canvas=this.refs.canvas;
  const can=canvas.getContext('2d');
  var no = 0;
  var poinTofill = -Math.PI/2;
  var diff;
  var time=5*60;
  var minutes=4;
  var second=59;

function cl(){
  diff = -Math.PI/2+((no / time) * Math.PI * 2);
  can.clearRect(0, 0, 300, 300);
  if(second===0){
      --minutes;
      second=60;
  }
  var t=`${minutes}:${second}`;
  can.textAlign = "center";
  can.font = "25px Monspace";
  can.fillText(t, 150, 150);
  can.lineWidth = 5;
  can.strokeStyle = "#fff";
  can.beginPath();
  can.arc(150, 150, 125, poinTofill, diff,false );
  can.stroke();
  can.save();
  if (no===time) {
      // clearTimeOut(fill);
  }
  ++no;
  --second;
}
}

draw(){
  const canvas=this.refs.canvas;
  const can=canvas.getContext('2d');
  var poinTofill = -Math.PI/2;
  var minutes=4;
  var second=59;
      var t=`${minutes}:${second}`;
      can.lineWidth = 6;
      can.fillStyle = "#fff";
      can.strokeStyle = "red";
      can.textAlign = "center";
      can.font = "25px Monspace";
      can.fillText(t, 150, 150);
      can.beginPath();
      can.arc(150, 150, 125, poinTofill, Math.PI*2, false)
      can.stroke();

}
trigger(){
  setInterval(this.circle(),1000) ;
  // alert("allll");
 }
  componentDidMount(){
      this.draw();
  }
  render(){
  return (<div>
    <canvas ref="canvas" width="300" height="300"></canvas>
    <div className="controler">
        <button ref="start" onClick={ this.trigger }>Start</button>
        <button id="stop">Stop</button>
        <button id="reset">Reset</button>
    </div>
    </div>
  )
}
}

export default clock;
