import React, { Component } from 'react';
import './App.css';

class App extends Component {
  constructor(props){
    super(props)
  this.state ={
    break:5,
    Session:30
  }
    this.increment=this.increment.bind(this);
    this.decrement=this.decrement.bind(this);
    this.breakSet= this.breakSet.bind(this);
    this.sessionSet=this.sessionSet.bind(this);
  }

  increment(value){
      return ++value;
  }
  decrement(value){
    return --value;
  }
  breakset(callback){
    let temp=callback(this.state.break);
    this.setState({
      break:temp
    })
  }
  sessionSet(callback){
    let temp=callback(this.state.session);
    this.setState({
      Session:temp
    })
  }

  render() {
    return (
      <div className="App">
      <h1 className="Head">Pomodoro</h1>
      <div className="Session">
      <div className="Break">
      <h1>Break</h1>
      <div className="timesetter">
      <button onClick={ this.breakSet(this.decrement) }>-</button>
      <p>{this.state.break}</p>
      <button onClick={ this.breakSet(this.increment) }>+</button>
      </div>
      </div>
      <div className="Working">
      <h1>Session</h1>
      <div className="timesetter">
      <button onClick={ this.sessionSet(this.decrement) }>-</button>
      <p>{this.state.Session}</p>
      <button onClick={ this.sessionSet(this.increment) }>+</button>
      </div>
      </div>
      </div>
      </div>
    );
  }
}

export default App;
